import React, { useState } from "react";
import "./signup.css";
import { NavLink } from "react-router-dom";
import axios from "axios";
// import Swal from 'sweetalert2';

const Sign_in = () => {
const [values, setValues] = useState({
  email : "",
  password : ""
});

  const config = {
    headers: {
      "content-type": "application/json",
    },
    withCredentials: true,
    credentials: "include",
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    console.log("hello1")

     axios.post('http://localhost:8050/api/customer/login',values,config)
     .then(res => {
      console.log("hello2")
      console.log("sttus",res.data.Status)
      if(res.data.Status === "Success"){
        console.log("hello3")
        // Cookies.set("token",res.data.token)
          // navigate('/products');
      } else {
        alert(res.data.Error)
      }
     })
     .then(err => console.log(err));
    }

  return (
    <section>
      <div className="sign_container">
        <div className="sign_header">
          <img
            src="data:image/png;base64,"
            alt="logo"
          />
        </div>
        <div className="sign_form">
          <form>
            <h1>Sign-in</h1>
            <div className="form_data">
              <label htmlFor="email">Email</label>
              <input
                type="text"
                onChange={e => setValues({...values,email : e.target.value})}
                // value={logdata.email}
                name="email"
                id="email"
              />
            </div>
            <div className="form_data">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                onChange={e => setValues({...values,password  : e.target.value})}
                // value={logdata.password} // iska use samajh nahi aaya
                placeholder="at least 6 characters"
                name="password"
                id="password"
              />
            </div>
            <button className="signin_btn" onClick={handleSubmit}>Continue</button>
          </form>
        </div>
        <div className="create_accountinfo">
          <p>New To Shopkaro</p>
          <NavLink to="/register">
            <button>Create Your Account</button>
          </NavLink>
        </div>
      </div>
    </section>
  );
};

export default Sign_in;
